import styled from 'styled-components';

export const Container = styled.div`
    display: inline-block;
`

export const PerInfo = styled.div`
    display: grid;
    margin: 20px;
    justify-items: center;
    align-items: center;
 
    .avartar{
        height: 50px;
        width: 50px;
        border-radius: 50%;
    }

    .comment {
        display: inline-block;
        margin: auto auto auto 30px;
        margin-left:  30px;
    }

`
